import { useEffect, useState } from "react";

export default function SearchFilter({ products, setProducts }) {
  const [filter, setFilter] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    let sortedProducts = [...products];

    if (filter) {
      sortedProducts = sortedProducts.filter((i) =>
        i.product.includes(searchTerm)
      );
    }
    setProducts(sortedProducts);
  }, [filter, searchTerm, products, setProducts]);

  return (
    <div>
      <input
        type="search"
        placeholder="Search"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select
        value={filter}
        onChange={(e) => setFilter(e.target.value === "true")}
      >
        <option value={true}>Filter On</option>
        <option value={false}>Filter Off</option>
      </select>
    </div>
  );
}
