import { useEffect, useState } from "react";

import ProductItem from "../ProductItem";
import AlphaSort from "../AlphaSort";

export default function Products() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error fetching products: ", error));
  }, []);

  // const AlphaSort = () => {
  //   const sortedProducts = [...products];
  //   if (order) {
  //     sortedProducts.sort((a, b) => a.title.localeCompare(b.title));
  //   } else {
  //     sortedProducts.sort((a, b) => b.title.localeCompare(a.title));
  //   }

  //   setProducts(sortedProducts);
  //   setOrder(!order);
  // };

  return (
    <div className="products-page page-container">
      <div className="products-header-wrapper">
        <h1>Products</h1>
        <AlphaSort products={products} setProducts={setProducts} />
        {/* <button className="-alpha-sort btn" onClick={AlphaSort}>
          {order ? "Sort Z-A" : "Sort A-Z"}
        </button> */}
      </div>
      <div className="products-grid">
        {products
          // .filter((product) => product.title.startsWith("A"))
          .map((product, index) => (
            <ProductItem product={product} key={index} />
          ))}
      </div>
    </div>
  );
}
