import { useContext, useState } from "react";
import { Link } from "react-router-dom";

import { CartContext } from "../context/CartContext";

export default function ProductItem(props) {
  const cartContext = useContext(CartContext);
  const { product, identity, setTotal } = props;
  const [quantity, setQuantity] = useState(1);

  const handleClick = () => {
    // setTotal((prevState) => prevState - quantity * product.price);
    cartContext.setCart((prevState) => {
      prevState.filter(
        (prevStateItem) => prevStateItem.id !== props.product.id
      );
    });
  };

  const addQuantity = () => {
    setQuantity(quantity + 1);
    setTotal((prevState) => parseFloat(prevState) + product.price);
  };

  const subQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
      setTotal((prevState) => parseFloat(prevState) - product.price);
    }
  };

  return (
    <div className={`product-card ${identity}`}>
      <div className="card-image">
        <Link to={`/products/${product.id}`}>
          <img
            src={product.image}
            alt={product.title}
            height="400px"
            width="auto"
          />
        </Link>
      </div>
      <div className="product-info">
        <h3>{product.title}</h3>
        <p>
          {product.description?.length > 100 && props.identity === false
            ? `${product.description.substring(0, 100)}...`
            : product.description}
        </p>
        <p>${product.price?.toFixed(2)}</p>
        <div className="cart-btn">
          {props.quantityBtns ? (
            <button
              onClick={() =>
                cartContext.setCart((prevState) => [...prevState, product])
              }
            >
              + Add Cart
            </button>
          ) : (
            <>
              <div className="cart-quantity">
                <button onClick={addQuantity}>+</button>
                <span>{quantity}</span>
                <button onClick={subQuantity}>-</button>
              </div>
              <div className="remove-btn">
                <button onClick={() => handleClick()}>- Remove</button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
