import { useEffect, useState } from "react";

export default function SearchFilter({ products, setProducts }) {
  const [filter, setFilter] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    let sortedProducts = [...products];

    if (filter) {
      sortedProducts = sortedProducts.filter((i) =>
        i.product.includes(searchTerm)
      );
    }
    setProducts(sortedProducts);
  }, [filter, searchTerm, products, setProducts]);

  return (
    <div>
      <input
        type="search"
        placeholder="Search"
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select defaultValue={searchTerm} onChange={setSearchTerm} />
    </div>
  );
}
