import { useState } from "react";

export default function Contact() {
  const [formSubmit, setFormSubmit] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // const firstName = e.target.querySelector("#first-name").value;
    // const lastName = e.target.querySelector("#last-name").value;

    // if (firstName.trim() === "" || lastName.trim() === "") {
    //   return message("Please fill in empty fields");
    // }

    setFormSubmit(true);

    setTimeout(() => {
      setFormSubmit(false);
    }, 5000);
  };

  return (
    <div className="contact-page page-container">
      <h1>QUESTIONS | COMMENTS</h1>

      <div className="contact-form-wrapper">
        <form onSubmit={handleSubmit}>
          <div className="form-input">
            <input
              type="text"
              id="first-name"
              placeholder="First Name"
              title="First Name"
            />
          </div>
          <div className="form-input">
            <input
              type="text"
              id="last-name"
              placeholder="Last Name"
              title="Last Name"
            />
          </div>
          <div className="form-input">
            <input type="email" id="email" placeholder="Email" title="Email" />
          </div>
          <div className="form-input">
            <textarea
              name="review"
              id="review"
              cols="40"
              rows="10"
              placeholder="Questions / Comments"
              title="Questions / Comments"
            ></textarea>
          </div>
          <div className="submit-btn">
            <button>Submit</button>
          </div>
        </form>
      </div>
      {formSubmit && (
        <div className="popup-message">
          <p>SUBMITTED!</p>
        </div>
      )}
    </div>
  );
}
